mapper.select:
		select u.*from ( select rownum as num, DEMANDINFOMATIONTBL.* from DEMANDINFOMATIONTBL where STATEID = 9
 	
 		
 		 and DEMANDTITLE like '%${name}%'
 		
 	
 	) u where u.num  between #{star} and #{end}

 select count(1) from DEMANDINFOMATIONTBL where STATEID != 7 
 
	and fromUserId = #{userid}
	
	
	and PARAMETERID = #{parameterid}
	
	
	and STATEID = #{stateid}
	
	
	and DEMANDTITLE like '%${name}%

	select u.*from ( select rownum as num, DEMANDINFOMATIONTBL.* from DEMANDINFOMATIONTBL where STATEID != 7 
 	
 		
 		and fromUserId = #{userid}
 		
 		
 		and PARAMETERID = #{parameterid}
 		
 		
 		and STATEID = #{stateid}
 		
 		
 		and DEMANDTITLE like '%${name}%
 		
 	
 	) u where u.num  between #{star} and #{end}

select * from demandInfomationTbl where stateId !=7 

and demandTitle like #{demandTitle}  

order by demandInfomationTbl.demandId

select * from( select rownum rn,m.* from (select demandInfomationTbl.*,tbParameter.parameterName from demandInfomationTbl,tbParameter where demandInfomationTbl.stateId=tbParameter.parameterId and stateId !=7
and demandTitle like #{demandTitle}
order by demandInfomationTbl.demandId)m where rownum <= (#{page}*5)) where rn >= ((#{page}-1)*5+1)

select * from ( select c.*,rownum rn from ( select * from counselorTbl where userAccount=userAccount
and auditState=#{state}
) c where rownum <= (#{page}*5)) where rn >= (#{page}*5-4)

select * from (
select e.*,rownum rn from (
select d.demandtitle,d.demanddetailinformation,u.useraccount,c.demandid,c.stateid from consultantsandneedstbl c,usertbl u,demandinfomationtbl d where c.employerid=u.userid  and c.demandid=d.demandid   and c.consultantid=#{userId}


 and c.Stateid=#{state}

) e
 where rownum <= (#{page}*5))
 where rn >= (#{page}*5-4)

select * from (
select e.*,rownum rn from (
select * from dailytbl where processid=#{parameterId}

 and DAILYREVIEW like #{state}

) e
 where rownum <= (#{page}*5))
 where rn >= (#{page}*5-4)
