mapper.select:
select * from ( select c.*,rownum rn from ( select * from informationTbl where informationid=informationid
and stateid=#{state}
) c where rownum <= (#{page}*5)) where rn >= (#{page}*5-4)

mapper.update:
 UPDATE informationTbl set informationid=#{informationId} 
 
 ,informationTitle=#{informationTitle}
 
  
  ,informationContent=#{informationContent}
 
  
  ,informationPublishTime=#{informationPublishTime}
 
  
  ,informationAddress=#{informationAddress}
 
  
  ,informationImg=#{informationImg}
 
 WHERE informationid=#{informationId}
