mapper.select:
 
	  SELECT * FROM ( SELECT A.*, ROWNUM RN FROM (	
	 	select * from PRODUCTIONTBL  where 1=1 
	 	
	 	 and productionName like #{fieldName}
			 and   userid in (select userid from usertbl where USERNAME like #{conditionName} )
  order by PRODUCTIONID asc
  ) A WHERE ROWNUM <=(#{currentPage}*#{eachPageCum}))  WHERE RN >=(1+(#{currentPage}-1)*#{eachPageCum})

 
	 
	 	select * from PRODUCTIONTBL  where 1=1 
	 	
	 	 and productionName like #{fieldName}
			 and   userid in (select userid from usertbl where USERNAME like #{conditionName} )

 
	  SELECT * FROM ( SELECT A.*, ROWNUM RN FROM (	
	 	select * from PRODUCTIONTBL  where 1=1  and   userid  =#{userName}
	 	
	 	 and productionName like #{fieldName}
			 and   userid in (select userid from usertbl where USERNAME like #{conditionName} )
  ) A WHERE ROWNUM <=(#{currentPage}*#{eachPageCum}))  WHERE RN >=(1+(#{currentPage}-1)*#{eachPageCum})

 
	 
	 	select * from PRODUCTIONTBL  where 1=1   and   userid  =#{userName}
	 	
	   and productionName like #{fieldName}
			 and   userid in (select userid from usertbl where USERNAME like #{conditionName} )

mapper.update:
UPDATE PRODUCTIONTBL SET productionName =#{productionName} 
	,parameterId=#{parameterId},productionMoney=#{productionMoney}
	,auditState=902,productionDetal=#{productionDetal}
	 , productionFile = #{productionFile}
	 , productionImage = #{productionImage}
WHERE PRODUCTIONID =#{productionId}

mapper.select:
	 
	   SELECT * FROM ( SELECT A.*, ROWNUM RN FROM (	
select * from PRODUCTIONTBL where PRODUCTIONID in (select PRODUCTIONID from PROANDUSER where USERID =#{userName})
 and productionName like #{fieldName}
			 and   userid in (select userid from usertbl where USERNAME like #{conditionName} )
  order by PRODUCTIONID asc
  ) A WHERE ROWNUM <=(#{currentPage}*#{eachPageCum}))  WHERE RN >=(1+(#{currentPage}-1)*#{eachPageCum})

 
	  SELECT * FROM ( SELECT A.*, ROWNUM RN FROM (	
	 	select * from PRODUCTIONTBL  where 1=1 and AUDITSTATE=903
	 	
	 	 and productionName like #{fieldName}
			 and   userid in (select userid from usertbl where USERNAME like #{conditionName} )
  order by PRODUCTIONID asc
  ) A WHERE ROWNUM <=(#{currentPage}*#{eachPageCum}))  WHERE RN >=(1+(#{currentPage}-1)*#{eachPageCum})

 
	 
	 	select * from PRODUCTIONTBL  where 1=1 and AUDITSTATE=903
	 	
	 	 and productionName like #{fieldName}
			 and   userid in (select userid from usertbl where USERNAME like #{conditionName} )
